package com.nested_recycler_view_netflix;

import android.os.Bundle;

import androidx.activity.EdgeToEdge;
import androidx.appcompat.app.AppCompatActivity;
import androidx.core.graphics.Insets;
import androidx.core.view.ViewCompat;
import androidx.core.view.WindowInsetsCompat;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.nested_recycler_view_netflix.Adapter.CategoryAdapter;
import com.nested_recycler_view_netflix.Models.Category;
import com.nested_recycler_view_netflix.Models.Movie;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

public class MainActivity extends AppCompatActivity {

    private RecyclerView categoryRecyclerView;
    private List<Category> categoryList = new ArrayList<>();

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        categoryRecyclerView = findViewById(R.id.categoryRecyclerView);

        setupData();

        CategoryAdapter categoryAdapter = new CategoryAdapter(this, categoryList);
        categoryRecyclerView.setLayoutManager(new LinearLayoutManager(this));
        categoryRecyclerView.setAdapter(categoryAdapter);
    }

    private void setupData() {
        List<Movie> popular = Arrays.asList(
                new Movie("Trending 1", "data:image/jpeg;base64,"),
                new Movie("Movie 2", "data:image/jpeg;base64,"),
                new Movie("Movie 3", "data:image/jpeg;base64,"),
                new Movie("Movie 4", "https://img.freepik.com/free-psd/music-shop-poster-template_23-2148855476.jpg?ga=GA1.1.1062262775.1734017500&semt=ais_items_boosted&w=740")
        );

        List<Movie> trending = Arrays.asList(
                new Movie("Trending 1", "data:image/jpeg;base64,"),
                new Movie("Trending 2", "data:image/jpeg;base64,"),
                new Movie("Trending 3", "data:image/jpeg;base64,"),
                new Movie("Trending 4", "data:image/jpeg;base64,")
        );

        categoryList.add(new Category("Popular", popular));
        categoryList.add(new Category("Trending Now", trending));
    }
}